import React from "react";
import './App.css';

class App extends React.Component {

  // Constructor 
  constructor(props) {
    super(props);
    this.state = {
        captcha: {},
        dataisLoaded: false
    };
  }

  // ComponentDidMount is used to call api from backend to get the captcha object
  // execute the code 
  componentDidMount() {
    fetch('http://localhost:8080/captcha/', {
      method: 'GET',
      headers: {
        accept: 'application/json',
      },
    })
      .then((res) => {
        if(res.headers.get("content-type") &&
          res.headers.get("content-type").toLowerCase().indexOf("application/json") >= 0) {
          return res.json()
        } else {
          throw new TypeError()
        }
      })
      .then((json) => {
          this.setState({
            captcha: json,
            dataisLoaded: true
          });
      })
      .catch(rejected => {
        console.log(rejected);
    });
  }

  selectImage(elem) {
    for (let i = 0; i < this.captcha?.images.length; i++) {
      if(this.captcha.images[i].idImage === elem.idImage){
        let test = document.getElementById(elem.idImage);
        test.classList.add('selected');
      }
    }
  }

  render() {
    const { dataisLoaded, captcha } = this.state;
    if (!dataisLoaded) return <div>
        <h1> Please wait some time.... </h1> </div> ;
    return (
      <div className = "App">
        <h1> Captcha App </h1>  
        <form className="form-group">
          <label>Question: {captcha.question} </label><br></br>
          <div className="row">
            <div className="grid">
              {
                captcha?.images.map((item) => ( 
                  <div class="cell" key={item.idImage}>
                    <img src={item.urlImage} alt={item.urlImage} data-uuid={item.idImage} onClick={this.selectImage(item)} />
                  </div>
                ))
              }
            </div>
          </div>
          <input type = "submit" value = "Send" className="btn btn-primary"/>
        </form>
    </div>
    );
  }
}

export default App;
