import React from "react";
import './App.css';

class App extends React.Component {

  // Constructor 
  constructor(props) {
    super(props);
    this.state = {
        captcha: {},
        DataisLoaded: false
    };
  }

  // ComponentDidMount is used to call api from backend to get the captcha object
  // execute the code 
  componentDidMount() {
    fetch("https://localhost:8080/captcha", { mode: 'cors' })
      .then((res) => res.json())
      .then((json) => {
          this.setState({
            captcha: json,
              DataisLoaded: true
          });
      })
      .catch(rejected => {
        console.log(rejected);
    });
  }

  selectImage() {
    console.log("in selectImage function")
  }

  render() {
    const { DataisLoaded, captcha } = this.state;
    if (!DataisLoaded) return <div>
        <h1> Please wait some time.... </h1> </div> ;

    return (
      <div className = "App">
        <h1> Captcha App </h1>  
        <form>
          <div className="row">
            <div className="form-group">
              <label>Question: </label>
              {
                captcha?.captcha.map((item) => ( 
                  <div class="cell">
                    <img src={item.urlImage} alt={item.urlImage} data-uuid={item.idImage} onclick="selectImage(this)" />
                  </div>
                ))
              }
              <input type="submit" value="Send" />
            </div>
          </div>
        </form>
    </div>
    );
  }
}

export default App;
