import React from 'react';
import Axios from 'axios';

export default function InscrireTheme() {
  
    const [listTheme, setListTheme] = useState([]);

    useEffect(() => {
        getListThemes();
    }, []);

  const setJeu =() => { (e) => {
  e.preventDefault();
  const elements = {nomJeu : document.getElementById("nomJeu"),idTheme : document.getElementById("themeSelect")}
  Axios.post('http://localhost:8080/jeu',elements).then(response => console.log(response));
    }
    }

  const getListThemes = () => {
        Axios.get('http://localhost:8080/theme/list').then((response) => {
            setListTheme(response.data)
        })
    }

    return(
        <div className="modal fade" id="inscrireTheme" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog" role="dialog">
                <div className="modal-content">
                    <div className="modal-header">
                        <button className="close" type="button" data-dismiss="modal" aria-label="Close">
                            <span aria-hidden="true">×</span>
                        </button>
                    </div>
                    <div className="modal-body">Création de votre Jeu</div>
                    <div className="modal-footer">
                    <div class="input-group mb-3">
                        <input type="text" class="form-control" placeholder="Nom du jeu" aria-label="Nom du jeu" aria-describedby="button-addon2" id="nomJeu"/>
                        <select class="form-select" id='themeSelect'>
                            <option selected>Choisir un theme</option>{
                            listTheme.map((val) =>
                                <option value={val.idTheme}>{val.nomTheme}</option>)
                            }
                        </select>
                        <button class="btn btn-outline-primary" type="button" id="button-addon2" onClick={setJeu}>Confirmer</button>
                    </div>
                    </div>
                </div>
            </div>
        </div>
    )

}
