import React from 'react';
import Axios from 'axios';

class InscrireTheme extends React.Component {
  
  jeu(){ (e) => {
  e.preventDefault();
  const elements = {theme : document.getElementById("nomTheme")}
  Axios.post('http://localhost:8080/jeu',elements).then(response => console.log(response));
    }
    }

  getThemes() {
    fetch('http://localhost:8080/listtheme/', {
      method: 'GET',
      headers: {
        accept: 'application/json',
      },
    }).then((res) => {
        if(res.headers.get("content-type") &&
          res.headers.get("content-type").toLowerCase().indexOf("application/json") >= 0) {
          return res.json();
        } else {
          throw new TypeError()
        }
      })
      .then((json) => {
          // create imagesSelected
          var themes = {
            idtheme: json?.idTheme,
            nomTheme: json?.nomTheme,
          };
          this.setState({
              theme : this.themes
          })
        }).catch(rejected => {
        console.log(rejected);
    });
    }

    render() {
        <div className="modal fade" id="inscrireTheme" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog" role="dialog">
                <div className="modal-content">
                    <div className="modal-header">
                        <button className="close" type="button" data-dismiss="modal" aria-label="Close">
                            <span aria-hidden="true">×</span>
                        </button>
                    </div>
                    <div className="modal-body">Création de votre theme</div>
                    <div className="modal-footer">
                    <div class="input-group mb-3">
                        <input type="text" class="form-control" placeholder="Nom du theme" aria-label="Nom du theme" aria-describedby="button-addon2" id="nomTheme"/>
                        <button class="btn btn-outline-primary" type="button" id="button-addon2" onClick={jeu}>Confirmer</button>
                    </div>
                    </div>
                </div>
            </div>
        </div>
    }

}
export default InscrireTheme;
