import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import { faTrashCan, faPenToSquare  } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

export default function ListUser() {

    const [lName, setLname] = useState('');
    const [fName, setFname] = useState('');
    const [listUser, setListUser] = useState([]);
    const [newLName, setnewLName] = useState('');
    const [newFName, setnewFName] = useState('');
    const [modalUpdateUser, setModalUpdateUser] = useState(false);

    useEffect(() => {
        getListUser();
    }, []);

    const getListUser = () => {
        Axios.get('http://localhost:8080/user/list').then((response) => {
            setListUser(response.data);
        })
    }

    const addUser = (e) => {
        e.preventDefault();
        Axios.post('http://localhost:8080/user/create', {
            lName: lName,
            fName: fName
        })
        alert('Data Inserted');
        getListUser();
    }

    const deleteUser = (e, id) => {
        e.preventDefault();
        Axios.delete(`http://localhost:8080/user/delete/${id}`).then((response) => {
            alert('Data Deleted');
            getListUser();
        })
        .catch(()=>{
            alert('Error');
        });
    }

    const updateUser = (e, id) => {
        e.preventDefault();
        Axios.update('http://localhost:8080/user/update', {
            lName: newLName,
            fName: newFName,
            id: id
        })
        alert('Data Updated');
        getListUser();
    }

    const styleModal = {
        display: 'none'
    }

    return (
        <div id="wrapper">
            <div className="container">
            <h4 className="mb-3 text-center mt-4">Liste des utilisateurs</h4>
                <div className="row">
                    <div className="col-sm-4">
                    <div className="box p-3 mb-3" style={{border:"1px solid #d0d0d0"}}>
                        <h6 className="mb-3 text-center mt-4">Créer un nouveau utilisateur</h6>
                        <form>
                            <div className="mb-3">
                                <label for="InputLname" className="form-label">Nom</label>
                                <input type="text" className="form-control" id="InputLname"
                                onChange={(e) => {setLname(e.target.value);}} />
                            </div>
                            <div className="mb-3">
                                <label for="InputFname" className="form-label">Prénom</label>
                                <input type="text" className="form-control" id="InputFname"
                                onChange={(e) => {setFname(e.target.value);}} />
                            </div>
                            <div className="d-flex justify-content-center">
                                <button onClick={addUser} type="submit" className="btn btn-primary">Valider</button>
                            </div>
                        </form>
                    </div>
                    </div>

                    <div className="col-sm-8">
                        <table id="example" className="table table-hover table-bordered">
                            <thead>
                                <tr>
                                <th width="10%">ID</th>
                                <th width="20%">Nom</th>
                                <th width="20%">Prénom</th>
                                <th width="20%">Identifiant</th>
                                <th width="10%">Action</th>
                                </tr>
                            </thead>
                            <tbody>
                            {listUser.map((val) =>
                                <tr>
                                <td>{val.idU}</td>
                                <td>{val.nomU}</td>
                                <td>{val.prenomU}</td>
                                <td>{val.username}</td>
                                <td>
                                    <a className="text-danger mr-2" onClick={(e) => {deleteUser(e, val.idU)}}><i><FontAwesomeIcon icon={faTrashCan} /></i></a>
                                    <a className="mr-2" href="#"><i><FontAwesomeIcon icon={faPenToSquare} data-toggle="modal" data-target="#updateUserModal" /></i></a>
                                </td>
                                </tr>)
                            }           
                            </tbody>
                        </table>
                    </div>
                </div>   
            </div>

            <div className="modal" id="updateUserModal">
            <div className="modal-dialog">
                <div className="modal-content">

                    <div className="modal-header">
                        <h4 className="modal-title">Modifier un utilisateur</h4>
                        <button type="button" className="close modelClose" data-dismiss="modal">&times;</button>
                    </div>

                    <div className="modal-body">
                        <div className="alert alert-danger alert-dismissible fade show" role="alert" style= {styleModal}>
                            <button type="button" className="close" data-dismiss="alert" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div className="alert alert-success alert-dismissible fade show" role="alert" style= {styleModal}>
                            <strong>L'utilisateur a été modifié avec succès.</strong>
                            <button type="button" className="close" data-dismiss="alert" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div id="updateUserBody">
                        <form>
                            <div className="mb-3">
                                <label for="InputLname" className="form-label">Nom</label>
                                <input type="text" className="form-control" id="InputLname"
                                onChange="" />
                            </div>
                            <div className="mb-3">
                                <label for="InputFname" className="form-label">Prénom</label>
                                <input type="text" className="form-control" id="InputFname"
                                onChange="" />
                            </div>
                        </form>
                        </div>
                    </div>           
                    <div className="modal-footer">
                        <button type="button" className="btn btn-primary mr-auto" id="submitUpdateUser">Modifier</button>
                        <button type="button" className="btn btn-secondary" data-dismiss="modal">Fermer</button>
                    </div>
                </div>
            </div>
        </div>                       
        </div>
    )


}
