import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import { faTrashCan,   } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

export default function ListUser() {

    const [listUser, setListUser] = useState('');

    useEffect(() => {
        getListUser();
    }, []);

    const getListUser = () => {
        Axios.get('http://localhost:8080/user/list').then((response) => {
            setListUser(response.data);
        })
    }

    return (
        <div id="wrapper">
            <div className="container">
                <h4 className="mb-3 text-center mt-4">Liste des utilisateurs</h4>
                    <div className="row">
                        <div className="col-sm-4">
                            <p>Test</p>
                        </div>
                        <div className="col-sm-8">
                            <table id="example" className="table table-hover table-bordered">
                                <thead>
                                    <tr>
                                    <th width="10%">ID</th>
                                    <th width="20%">Nom</th>
                                    <th width="20%">Pr√©nom</th>
                                    <th width="20%">Identifiant</th>
                                    <th width="20%">Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                {listUser.map((val) =>
                                    <tr>
                                    <td>{val.idU}</td>
                                    <td>{val.nomU}</td>
                                    <td>{val.prenomU}</td>
                                    <td>{val.username}</td>
                                    <td>
                                        <a className="text-danger mr-2" onClick=""><i><FontAwesomeIcon icon={faTrashCan} /></i></a>
                                    </td>
                                    </tr>)
                                }           
                                </tbody>
                            </table>
                        </div>
                    </div>


            </div>
        </div>
    )


}
