import React from "react";
import './App.css';

class App extends React.Component {

  // Constructor 
  constructor(props) {
    super(props);
    this.state = {
        captcha: {},
        DataisLoaded: false
    };
  }

  // ComponentDidMount is used to call api from backend to get the captcha object
  // execute the code 
  componentDidMount() {
    fetch('http://localhost:8080/captcha/', {
      method: 'GET',
      headers: {
        accept: 'application/json',
      },
    })
      .then((res) => {
        if(res.headers.get("content-type") &&
          res.headers.get("content-type").toLowerCase().indexOf("application/json") >= 0) {
          return res.json()
        } else {
          throw new TypeError()
        }
      })
      .then((json) => {
          this.setState({
            captcha: json,
            DataisLoaded: true
          });
      })
      .catch(rejected => {
        console.log(rejected);
    });
  }

  selectImage() {
    console.log("in selectImage function")
  }

  render() {
    const { DataisLoaded, captcha } = this.state;
    if (!DataisLoaded) return <div>
        <h1> Please wait some time.... </h1> </div> ;

    let test = {"idQuestion":5,"question":"Saurez-vous reconnaître le chat de Thomas Pesquet ?","captcha":[{"idImage":11,"urlImage":"/images/neutres/chat_neutre_11.jpg"},{"idImage":6,"urlImage":"/images/neutres/chat_neutre_6.jpg"},{"idImage":3,"urlImage":"/images/neutres/chat_neutre_3.jpg"},{"idImage":4,"urlImage":"/images/neutres/chat_neutre_4.jpg"},{"idImage":10,"urlImage":"/images/neutres/chat_neutre_10.jpg"},{"idImage":2,"urlImage":"/images/neutres/chat_neutre_2.jpg"},{"idImage":1,"urlImage":"/images/neutres/chat_neutre_1.jpg"},{"idImage":13,"urlImage":"/images/neutres/chat_neutre_13.jpg"},{"idImage":18,"urlImage":"/images/singuliers/chat_cosmonaute.jpg"}]};
    return (
      <div className = "App">
        <h1> Captcha App </h1>  
        <form className="form-group">
          <div className="row">
            <div className="grid">
              <label>Question: {captcha.question} </label>
              {
                test?.captcha.map((item) => ( 
                  <div class="cell" key={item.idImage}>
                    <img src={item.urlImage} alt={item.urlImage} data-uuid={item.idImage} onclick="selectImage(this)" />
                  </div>
                ))
              }
              <input type="submit" value="Send" />
            </div>
          </div>
        </form>
    </div>
    );
  }
}

export default App;
