import React from "react";
import './App.css';

class App extends React.Component {

  // Constructor 
  constructor(props) {
    super(props);

    this.state = {
        captcha: {},
        DataisLoaded: false
    };
  }

  // ComponentDidMount is used to call api from backend to get the captcha object
  // execute the code 
  componentDidMount() {
    fetch("http://localhost:8080/captcha")
      .then((res) => res.json())
      .then((json) => {
          this.setState({
            captcha: json,
              DataisLoaded: true
          });
      })
  }

  selectImage() {
    console.log("go to select an image")
  }

  render() {
    const { DataisLoaded, items } = this.state;
    if (!DataisLoaded) return <div>
        <h1> Please wait some time.... </h1> </div> ;

    return (
      <div className = "App">
        <header className="App-header">
          Captcha App
        </header>
        <h1> Fetch data from an api in react </h1>  {
            this.captcha?.captcha.map((item) => ( 
              <div class="cell">
                <img src={item.urlImage} alt={item.urlImage} data-uuid={item.idImage} onclick="selectImage(this)" />
              </div>
            ))
        }
        <form>
            <div className="row">
              <div className="form-group">
                <label>Question: </label>


                <input type="submit" value="Send" />
              </div>
            </div>

          </form>
    </div>
    );
  }
}

export default App;
